CREATE TABLE users ( id INT UNSIGNED NOT NULL AUTO_INCREMENT, userId VARCHAR(60) NOT NULL, userName VARCHAR(40) NOT NULL, userPassword CHAR(60) NOT NULL, firstname VARCHAR(40) NOT NULL, lastname VARCHAR(40) NOT NULL, service VARCHAR(30) NOT NULL, email VARCHAR(40) NOT NULL, aboutMe TEXT, isMod BOOLEAN DEFAULT 0, date_logout DATETIME, PRIMARY KEY (id) )ENGINE=INNODB DEFAULT CHARSET=utf8;
CREATE TABLE publications ( id INT UNSIGNED NOT NULL AUTO_INCREMENT, userId VARCHAR(60) NOT NULL, date_publication DATETIME NOT NULL, title VARCHAR(60) NOT NULL, content TEXT NOT NULL, numberComments INT DEFAULT 0, likes INT DEFAULT 0, userName VARCHAR(40) NOT NULL, modified BOOLEAN DEFAULT 0, date_modif DATETIME, moderated BOOLEAN DEFAULT 0, viewed BOOLEAN DEFAULT 1, PRIMARY KEY (id) )ENGINE=INNODB DEFAULT CHARSET=utf8;